import React from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";

export default function Navbar() {
    const navigate = useNavigate();
    const location = useLocation();
    const isAuth = localStorage.getItem("token");

    const isDashboard = location.pathname === "/dashboard";

    function handleLogout() {
        localStorage.removeItem("token");
        navigate("/login");
    }

    return (
        <nav
            className={`p-4 flex justify-between items-center transition-all duration-300 ${isDashboard
                    ? "bg-transparent text-gray-900 shadow-none"
                    : "bg-orange-600 text-white shadow-md"
                }`}
            style={
                isDashboard
                    ? {
                        backdropFilter: "blur(10px)",
                        WebkitBackdropFilter: "blur(10px)",
                    }
                    : {}
            }
        >
            <Link
                to="/"
                className={`text-2xl font-bold ${isDashboard ? "text-gray-900" : "text-white"
                    }`}
            >
                EduTrack
            </Link>
            <div className="space-x-4">
                <Link
                    to="/"
                    className={`hover:underline ${isDashboard ? "text-gray-900" : "text-white"
                        }`}
                >
                    Home
                </Link>
                {isAuth ? (
                    <>
                        <Link
                            to="/dashboard"
                            className={`hover:underline ${isDashboard ? "text-gray-900" : "text-white"
                                }`}
                        >
                            Dashboard
                        </Link>
                        <button
                            onClick={handleLogout}
                            className={`px-3 py-1 rounded font-medium transition ${isDashboard
                                    ? "bg-gray-800 text-white hover:bg-gray-700"
                                    : "bg-white text-orange-600 hover:bg-gray-100"
                                }`}
                        >
                            Logout
                        </button>
                    </>
                ) : (
                    <>
                        <Link
                            to="/login"
                            className={`hover:underline ${isDashboard ? "text-gray-900" : "text-white"
                                }`}
                        >
                            Login
                        </Link>
                        <Link
                            to="/register"
                            className={`hover:underline ${isDashboard ? "text-gray-900" : "text-white"
                                }`}
                        >
                            Register
                        </Link>
                    </>
                )}
            </div>
        </nav>
    );
}
