import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function CourseDetail() {
  const { id } = useParams();
  const nav = useNavigate();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);
  const [progress, setProgress] = useState([]);
  const [enrolled, setEnrolled] = useState(false);
  const [isUpcoming, setIsUpcoming] = useState(false);

  useEffect(() => {
    checkAuth();
    fetchCourse();
    fetchLessons();
    fetchProgress();
    checkEnrollment();
  }, [id]);

  // âœ… Redirect if not logged in
  function checkAuth() {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to access courses');
      nav('/login');
    }
  }

  async function fetchCourse() {
    try {
      const res = await API.get(`/courses/${id}`);
      setCourse(res.data);

      // ðŸ”’ Check if upcoming (hasn't started)
      if (new Date(res.data.start_date) > new Date()) {
        setIsUpcoming(true);
      }
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchLessons() {
    try {
      const res = await API.get(`/lessons/${id}`);
      setLessons(res.data);
      if (res.data[0]) setSelected(res.data[0]);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchProgress() {
    try {
      const res = await API.get(`/progress/course/${id}`);
      setProgress(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function checkEnrollment() {
    try {
      const res = await API.get('/enrollments');
      const enrolledCourses = res.data.map((c) => c.id);
      setEnrolled(enrolledCourses.includes(Number(id)));
    } catch (e) {
      console.error(e);
    }
  }

  async function enroll() {
    try {
      await API.post('/enrollments', { course_id: id });
      alert('âœ… Enrolled successfully!');
      setEnrolled(true);
    } catch (e) {
      alert('Error enrolling in course');
    }
  }

  async function markDone(lessonId) {
    try {
      await API.post(`/progress/lesson/${lessonId}/complete`);
      fetchProgress();
      alert('Marked complete');
    } catch (e) {
      console.error(e);
      alert('Error');
    }
  }

  function isUnlocked(l) {
    if (!l.release_date) return true;
    return new Date(l.release_date) <= new Date();
  }

  // ðŸŸ¡ UI: upcoming course message
  const upcomingBanner = isUpcoming ? (
    <div
      style={{
        backgroundColor: '#fff3cd',
        color: '#856404',
        padding: 10,
        borderRadius: 6,
        marginBottom: 15,
        border: '1px solid #ffeeba',
      }}
    >
      âš  This course hasnâ€™t started yet. You can preview lessons, but content is locked.
    </div>
  ) : null;

  return (
    <div style={{ display: 'flex', gap: 20, padding: 20 }}>
      <div style={{ width: 320 }}>
        <h2>{course?.title}</h2>
        <p>{course?.description}</p>

        {!enrolled && !isUpcoming && (
          <div style={{ marginTop: 20 }}>
            <p style={{ color: 'red' }}>âš  You must enroll to access course materials.</p>
            <button onClick={enroll}>Enroll Now</button>
          </div>
        )}

        {upcomingBanner}

        <h3>Lessons</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {lessons.map((l) => (
            <li
              key={l.id}
              style={{
                padding: 8,
                cursor: 'pointer',
                background: selected?.id === l.id ? '#eee' : 'transparent',
                marginBottom: 6,
              }}
              onClick={() => {
                if (!isUpcoming && isUnlocked(l)) setSelected(l);
              }}
            >
              {l.position}. {l.title}{' '}
              {!isUnlocked(l) && (
                <span style={{ color: '#999' }}>
                  (Locked until {new Date(l.release_date).toLocaleString()})
                </span>
              )}
              {isUpcoming && <span style={{ color: '#999' }}> ðŸ”’ Upcoming</span>}
              {progress.includes(l.id) && <span style={{ color: 'green', marginLeft: 8 }}>âœ“</span>}
            </li>
          ))}
        </ul>
      </div>

      <div style={{ flex: 1 }}>
        {selected ? (
          <>
            <h3>{selected.title}</h3>

            {/* ðŸ”’ If upcoming, block content */}
            {isUpcoming ? (
              <div
                style={{
                  textAlign: 'center',
                  padding: 40,
                  color: '#666',
                  background: '#f9f9f9',
                  border: '1px solid #ddd',
                  borderRadius: 8,
                }}
              >
                <p>ðŸš« Content locked until course starts on {new Date(course.start_date).toLocaleDateString()}</p>
              </div>
            ) : (
              <>
                {selected.content_type === 'video' && (
                  <video width="100%" height="480" controls>
                    <source
                      src={
                        selected.content_url.startsWith('http')
                          ? selected.content_url
                          : `http://localhost:5001${selected.content_url}`
                      }
                      type="video/mp4"
                    />
                    Your browser does not support video.
                  </video>
                )}
                {selected.content_type === 'pdf' && (
                  <iframe
                    src={
                      selected.content_url.startsWith('http')
                        ? selected.content_url
                        : `http://localhost:5001${selected.content_url}`
                    }
                    width="100%"
                    height="600"
                    title={selected.title}
                  />
                )}
                {selected.content_type === 'text' && (
                  <div dangerouslySetInnerHTML={{ __html: selected.content_url }} />
                )}

                <div style={{ marginTop: 12 }}>
                  <button onClick={() => markDone(selected.id)}>Mark Completed</button>
                </div>
              </>
            )}
          </>
        ) : (
          <p>Select a lesson</p>
        )}
      </div>
    </div>
  );
}
