import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api";

export default function CourseDetail() {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);
  const [progress, setProgress] = useState([]);

  useEffect(() => {
    fetchCourse();
    fetchLessons();
    fetchProgress();
  }, [id]);

  async function fetchCourse() {
    try {
      const res = await API.get(`/courses/${id}`);
      setCourse(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchLessons() {
    try {
      const res = await API.get(`/lessons/${id}`);
      setLessons(res.data);
      if (res.data[0]) setSelected(res.data[0]);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchProgress() {
    try {
      const res = await API.get(`/progress/course/${id}`);
      setProgress(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function markDone(lessonId) {
    try {
      await API.post(`/progress/lesson/${lessonId}/complete`);
      fetchProgress();
      alert("Marked complete ✅");
    } catch (e) {
      console.error(e);
      alert("Error");
    }
  }

  function isUnlocked(l) {
    if (!l.release_date) return true;
    return new Date(l.release_date) <= new Date();
  }

  return (
    <div className="flex gap-6 p-6">
      {/* Sidebar Lessons */}
      <div className="w-80 bg-white shadow rounded-lg p-4">
        <h2 className="text-xl font-bold mb-2">{course?.title}</h2>
        <p className="text-gray-600 mb-4">{course?.description}</p>
        <h3 className="font-semibold mb-2">Lessons</h3>
        <ul className="space-y-2">
          {lessons.map((l) => (
            <li
              key={l.id}
              onClick={() => (isUnlocked(l) ? setSelected(l) : null)}
              className={`p-2 rounded cursor-pointer ${
                selected?.id === l.id
                  ? "bg-orange-100 border border-orange-400"
                  : "hover:bg-gray-100"
              }`}
            >
              <div className="flex justify-between items-center">
                <span>
                  {l.position}. {l.title}{" "}
                  {!isUnlocked(l) && (
                    <span className="text-sm text-gray-500">
                      (Locked until {new Date(l.release_date).toLocaleString()})
                    </span>
                  )}
                </span>
                {progress.includes(l.id) && (
                  <span className="text-green-600 font-bold">✓</span>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* Content Area */}
      <div className="flex-1 bg-white shadow rounded-lg p-6">
        {selected ? (
          <>
            <h3 className="text-2xl font-bold mb-4">{selected.title}</h3>

            {selected.content_type === "video" && (
              <video className="w-full rounded-lg" height="480" controls>
                <source
                  src={
                    selected.content_url.startsWith("http")
                      ? selected.content_url
                      : `http://localhost:5001${selected.content_url}`
                  }
                  type="video/mp4"
                />
                Your browser does not support video.
              </video>
            )}

            {selected.content_type === "pdf" && (
              <iframe
                src={
                  selected.content_url.startsWith("http")
                    ? selected.content_url
                    : `http://localhost:5001${selected.content_url}`
                }
                width="100%"
                height="600"
                title={selected.title}
                className="rounded border"
              />
            )}

            {selected.content_type === "text" && (
              <div
                className="prose max-w-full"
                dangerouslySetInnerHTML={{ __html: selected.content_url }}
              />
            )}

            <div className="mt-6">
              <button
                onClick={() => markDone(selected.id)}
                className="bg-orange-600 text-white px-4 py-2 rounded hover:bg-orange-700"
              >
                Mark Completed
              </button>
            </div>
          </>
        ) : (
          <p className="text-gray-500">Select a lesson from the sidebar</p>
        )}
      </div>
    </div>
  );
}
