import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function CourseDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);
  const [progress, setProgress] = useState([]);
  const [isEnrolled, setIsEnrolled] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to access this course.');
      navigate('/login');
      return;
    }
    checkEnrollment();
    fetchCourse();
    fetchLessons();
  }, [id]);

  async function checkEnrollment() {
    try {
      const res = await API.get('/enrollments');
      const enrolledIds = res.data.map(c => c.id);
      if (!enrolledIds.includes(parseInt(id))) {
        alert('You must enroll in this course to access lessons.');
        navigate('/dashboard');
      } else {
        setIsEnrolled(true);
      }
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchCourse() {
    try {
      const res = await API.get(`/courses/${id}`);
      setCourse(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchLessons() {
    try {
      const res = await API.get(`/lessons/${id}`);
      setLessons(res.data);
      if (res.data[0]) setSelected(res.data[0]);
    } catch (e) {
      console.error(e);
    }
  }

  async function markDone(lessonId) {
    try {
      await API.post(`/progress/lesson/${lessonId}/complete`);
      alert('Marked as complete!');
    } catch (e) {
      console.error(e);
    }
  }

  if (!isEnrolled) return null;

  return (
    <div style={{ display: 'flex', gap: 20, padding: 20, background: '#f9fafb', minHeight: '100vh' }}>
      <div style={{ width: 300, background: '#fff', borderRadius: 8, padding: 12, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
        <h2 style={{ color: '#f97316' }}>{course?.title}</h2>
        <p style={{ fontSize: 13, color: '#6b7280' }}>{course?.description}</p>
        <h3 style={{ marginTop: 20, marginBottom: 10 }}>Lessons</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {lessons.map(l => (
            <li
              key={l.id}
              onClick={() => setSelected(l)}
              style={{
                padding: 8,
                marginBottom: 6,
                background: selected?.id === l.id ? '#fde68a' : '#fff',
                borderRadius: 6,
                cursor: 'pointer',
                border: '1px solid #e5e7eb',
              }}
            >
              {l.position}. {l.title}
            </li>
          ))}
        </ul>
      </div>

      <div style={{ flex: 1, background: '#fff', borderRadius: 8, padding: 20, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
        {selected ? (
          <>
            <h3 style={{ color: '#111827' }}>{selected.title}</h3>
            {selected.content_type === 'video' && (
              <video width="100%" height="480" controls>
                <source
                  src={
                    selected.content_url.startsWith('http')
                      ? selected.content_url
                      : `http://localhost:5001${selected.content_url}`
                  }
                  type="video/mp4"
                />
                Your browser does not support video playback.
              </video>
            )}
            {selected.content_type === 'pdf' && (
              <iframe
                src={
                  selected.content_url.startsWith('http')
                    ? selected.content_url
                    : `http://localhost:5001${selected.content_url}`
                }
                width="100%"
                height="600"
                title={selected.title}
              />
            )}
            {selected.content_type === 'text' && (
              <div dangerouslySetInnerHTML={{ __html: selected.content_url }} />
            )}
            <button
              onClick={() => markDone(selected.id)}
              style={{
                marginTop: 12,
                backgroundColor: '#10b981',
                color: '#fff',
                padding: '6px 12px',
                border: 'none',
                borderRadius: 6,
                cursor: 'pointer',
              }}
            >
              Mark as Completed
            </button>
          </>
        ) : (
          <p>Select a lesson from the list</p>
        )}
      </div>
    </div>
  );
}
