import React, { useEffect, useState } from "react";
import axios from "../api"; // or adjust path
import { Link } from "react-router-dom";

const Dashboard = ({ userId }) => {
  const [enrolled, setEnrolled] = useState([]);
  const [upcoming, setUpcoming] = useState([]);

  useEffect(() => {
    axios.get(`/dashboard/${userId}`).then(res => {
      setEnrolled(res.data.enrolled);
      setUpcoming(res.data.upcoming);
    });
  }, [userId]);

  return (
    <div>
      <h2>Your Enrolled Courses</h2>
      {enrolled.map(course => (
        <div key={course.id}>
          <h4>{course.title}</h4>
          <Link to={`/course/${course.id}`}>Go to Course</Link>
        </div>
      ))}

      <h2>Upcoming Courses</h2>
      {upcoming.map(course => (
        <div key={course.id}>
          <h4>{course.title}</h4>
          <p>Starts: {new Date(course.start_date).toLocaleDateString()}</p>
          {new Date() >= new Date(course.start_date) ? (
            <Link to={`/course/${course.id}`}>Go to Course</Link>
          ) : (
            <button disabled>Locked</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
