import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import API from '../api';

export default function Dashboard() {
  const [courses, setCourses] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [enrolled, setEnrolled] = useState([]);

  useEffect(()=>{ fetchAll(); fetchUpcoming(); fetchEnrolled(); },[]);

  async function fetchAll(){ try{ const res = await API.get('/courses'); setCourses(res.data); }catch(e){ console.error(e);} }
  async function fetchUpcoming(){ try{ const res = await API.get('/courses/upcoming'); setUpcoming(res.data);}catch(e){console.error(e);} }
  async function fetchEnrolled(){ try{ const res = await API.get('/enrollments'); setEnrolled(res.data);}catch(e){console.error(e);} }

  async function enroll(courseId){
    try{
      const res = await API.post('/enrollments', { course_id: courseId });
      alert(res.data.message);
      fetchEnrolled();
    }catch(err){ console.error(err); alert('Error enrolling'); }
  }

  return (
    <div style={{padding:20}}>
      <h2>Dashboard</h2>

      <h3>Your Enrolled Courses</h3>
      <ul>{enrolled.length?enrolled.map(c=> <li key={c.id}><Link to={`/course/${c.id}`}>{c.title}</Link></li>) : <li>No enrolled courses</li>}</ul>

      <h3>All Courses</h3>
      <ul>{courses.map(c=> <li key={c.id}><Link to={`/course/${c.id}`}>{c.title} â€” {c.instructor}</Link>{" "}<button onClick={()=>enroll(c.id)}>Enroll</button></li>)}</ul>

      <h3>Upcoming</h3>
      <ul>{upcoming.map(u=> <li key={u.id}>{u.title} (starts {u.start_date})</li>)}</ul>
    </div>
  );
}
