import React from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";

export default function Navbar() {
    const navigate = useNavigate();
    const location = useLocation();
    const isAuth = localStorage.getItem("token");

    const isDashboard = location.pathname === "/dashboard";

    function handleLogout() {
        localStorage.removeItem("token");
        navigate("/login");
    }

    return (
        <nav
            className={`w-full fixed top-0 left-0 z-50 transition-all duration-300 ${isDashboard
                    ? "backdrop-blur-md bg-white/70 shadow-sm"
                    : "bg-gradient-to-r from-orange-600 to-amber-500 shadow-md"
                }`}
        >
            <div className="max-w-7xl mx-auto flex justify-between items-center px-6 py-3">
                {/* Brand */}
                <Link
                    to="/"
                    className={`text-2xl font-extrabold tracking-tight ${isDashboard ? "text-gray-800" : "text-white"
                        }`}
                >
                    EduTrack
                </Link>

                {/* Nav Links */}
                <div className="flex items-center space-x-6">
                    <Link
                        to="/"
                        className={`font-medium hover:underline ${isDashboard ? "text-gray-800" : "text-white"
                            }`}
                    >
                        Home
                    </Link>

                    <Link
                        to="/dashboard"
                        className={`font-medium hover:underline ${isDashboard ? "text-gray-800" : "text-white"
                            }`}
                    >
                        Dashboard
                    </Link>

                    {isAuth ? (
                        <button
                            onClick={handleLogout}
                            className={`px-4 py-1.5 rounded-lg font-semibold transition ${isDashboard
                                    ? "bg-gray-800 text-white hover:bg-gray-700"
                                    : "bg-white text-orange-600 hover:bg-gray-100"
                                }`}
                        >
                            Logout
                        </button>
                    ) : (
                        <>
                            <Link
                                to="/login"
                                className={`font-medium hover:underline ${isDashboard ? "text-gray-800" : "text-white"
                                    }`}
                            >
                                Login
                            </Link>
                            <Link
                                to="/register"
                                className={`font-medium hover:underline ${isDashboard ? "text-gray-800" : "text-white"
                                    }`}
                            >
                                Register
                            </Link>
                        </>
                    )}
                </div>
            </div>
        </nav>
    );
}
