import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api';

export default function Dashboard() {
  const [courses, setCourses] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [enrolled, setEnrolled] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to access courses.');
      navigate('/login');
      return;
    }
    fetchCourses();
    fetchEnrolled();
  }, []);

  async function fetchCourses() {
    try {
      const res = await API.get('/courses');
      const now = new Date();
      const active = res.data.filter(c => new Date(c.start_date) <= now);
      const future = res.data.filter(c => new Date(c.start_date) > now);
      setCourses(active);
      setUpcoming(future);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchEnrolled() {
    try {
      const res = await API.get('/enrollments');
      setEnrolled(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function enroll(courseId) {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first.');
      navigate('/login');
      return;
    }
    try {
      await API.post('/enrollments', { course_id: courseId });
      alert('Enrolled successfully!');
      fetchEnrolled();
    } catch (e) {
      alert(e?.response?.data?.message || 'Error');
    }
  }

  function isEnrolled(courseId) {
    return enrolled.some(c => c.id === courseId);
  }

  function goToCourse(courseId) {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to access the course.');
      navigate('/login');
      return;
    }
    const enrolledCourse = enrolled.some(c => c.id === courseId);
    if (!enrolledCourse) {
      alert('Please enroll first to access the course content.');
      return;
    }
    navigate(`/course/${courseId}`);
  }

  return (
    <div style={{ padding: 20, fontFamily: "'Segoe UI', sans-serif", backgroundColor: '#f8fafc', minHeight: '100vh' }}>
      <h1 style={{ color: '#f97316', fontSize: '2.5rem', fontWeight: 700, marginBottom: 30 }}>EduTrack</h1>

      {/* Enrolled Courses */}
      <section style={{ marginBottom: 40 }}>
        <h2 style={{ fontSize: '1.8rem', color: '#1e293b' }}>Your Enrolled Courses</h2>
        {enrolled.length === 0 ? (
          <p style={{ color: '#6b7280' }}>You haven't enrolled in any course yet.</p>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(300px,1fr))', gap: 20, marginTop: 15 }}>
            {enrolled.map(c => (
              <div key={c.id} style={{ backgroundColor: '#fff', padding: 16, borderRadius: 10, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
                <h3 style={{ fontSize: '1.2rem', fontWeight: 600, color: '#0f172a' }}>{c.title}</h3>
                <p style={{ fontSize: 13, color: '#475569' }}>{c.instructor}</p>
                <p style={{ marginTop: 5, fontSize: 13, color: '#64748b' }}>{c.description?.slice(0, 120)}...</p>
                <button onClick={() => goToCourse(c.id)} style={{ marginTop: 10, backgroundColor: '#10b981', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: 6, cursor: 'pointer' }}>Go to Course</button>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Active Courses */}
      <section style={{ marginBottom: 40 }}>
        <h2 style={{ fontSize: '1.8rem', color: '#1e293b' }}>Available Courses</h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(300px,1fr))', gap: 20, marginTop: 15 }}>
          {courses.map(c => (
            <div key={c.id} style={{ backgroundColor: '#fff', padding: 16, borderRadius: 10, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
              <h3 style={{ fontSize: '1.2rem', fontWeight: 600, color: '#0f172a' }}>{c.title}</h3>
              <p style={{ fontSize: 13, color: '#475569' }}>{c.instructor}</p>
              <p style={{ marginTop: 5, fontSize: 13, color: '#64748b' }}>{c.description?.slice(0, 120)}...</p>
              <div style={{ marginTop: 10 }}>
                {isEnrolled(c.id) ? (
                  <button onClick={() => goToCourse(c.id)} style={{ backgroundColor: '#10b981', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: 6, cursor: 'pointer' }}>Go to Course</button>
                ) : (
                  <button onClick={() => enroll(c.id)} style={{ backgroundColor: '#f97316', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: 6, cursor: 'pointer' }}>Enroll</button>
                )}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Upcoming Courses */}
      <section>
        <h2 style={{ fontSize: '1.8rem', color: '#1e293b' }}>Upcoming Courses</h2>
        {upcoming.length === 0 ? (
          <p style={{ color: '#6b7280' }}>No upcoming courses right now.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, display: 'grid', gap: 12, marginTop: 15 }}>
            {upcoming.map(u => (
              <li key={u.id} style={{ backgroundColor: '#fff', padding: 14, borderRadius: 10, boxShadow: '0 2px 8px rgba(0,0,0,0.1)', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>{u.title} â€” Starts {new Date(u.start_date).toLocaleDateString()}</span>
                <button disabled style={{ backgroundColor: '#9ca3af', color: '#fff', border: 'none', padding: '6px 12px', borderRadius: 6, cursor: 'not-allowed' }}>Locked</button>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}
