import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function CourseDetail() {
  const { id } = useParams();
  const nav = useNavigate();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);
  const [progress, setProgress] = useState([]);
  const [enrolled, setEnrolled] = useState(false);
  const [isUpcoming, setIsUpcoming] = useState(false);

  useEffect(() => {
    checkAuth();
    loadCourseData();
  }, [id]);

  // âœ… Redirect to login if not authenticated
  function checkAuth() {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to access courses');
      nav('/login');
    }
  }

  async function loadCourseData() {
    try {
      const resCourse = await API.get(`/courses/${id}`);
      setCourse(resCourse.data);

      // Check if upcoming
      const isFuture = new Date(resCourse.data.start_date) > new Date();
      setIsUpcoming(isFuture);

      const resLessons = await API.get(`/lessons/${id}`);
      setLessons(resLessons.data);
      if (resLessons.data.length > 0) setSelected(resLessons.data[0]);

      const resEnroll = await API.get('/enrollments');
      const enrolledIds = resEnroll.data.map(c => c.id);
      setEnrolled(enrolledIds.includes(Number(id)));

      const resProgress = await API.get(`/progress/course/${id}`);
      setProgress(resProgress.data);
    } catch (e) {
      console.error('Error loading course:', e);
    }
  }

  async function enroll() {
    try {
      await API.post('/enrollments', { course_id: id });
      alert('âœ… Enrolled successfully!');
      setEnrolled(true);
    } catch (e) {
      alert(e?.response?.data?.message || 'Error enrolling in course');
    }
  }

  async function markDone(lessonId) {
    try {
      await API.post(`/progress/lesson/${lessonId}/complete`);
      fetchProgress();
      alert('Lesson marked as complete');
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchProgress() {
    try {
      const res = await API.get(`/progress/course/${id}`);
      setProgress(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  function isUnlocked(lesson) {
    if (!lesson.release_date) return true;
    return new Date(lesson.release_date) <= new Date();
  }

  if (!course) return <p style={{ padding: 20 }}>Loading...</p>;

  return (
    <div style={{ display: 'flex', gap: 20, padding: 20 }}>
      {/* Sidebar */}
      <div style={{ width: 320 }}>
        <h2>{course.title}</h2>
        <p>{course.description}</p>

        {isUpcoming && (
          <div
            style={{
              backgroundColor: '#fff3cd',
              color: '#856404',
              padding: 10,
              borderRadius: 6,
              marginBottom: 15,
              border: '1px solid #ffeeba',
            }}
          >
            âš  This course hasnâ€™t started yet. You can preview lessons, but content is locked.
          </div>
        )}

        {!enrolled && !isUpcoming && (
          <div style={{ marginTop: 20 }}>
            <p style={{ color: 'red' }}>âš  You must enroll to access this course.</p>
            <button onClick={enroll}>Enroll Now</button>
          </div>
        )}

        <h3>Lessons</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {lessons.map((lesson) => (
            <li
              key={lesson.id}
              style={{
                padding: 8,
                cursor: isUpcoming ? 'not-allowed' : 'pointer',
                background: selected?.id === lesson.id ? '#eee' : 'transparent',
                marginBottom: 6,
              }}
              onClick={() => {
                if (!isUpcoming && isUnlocked(lesson)) setSelected(lesson);
              }}
            >
              {lesson.position}. {lesson.title}
              {isUpcoming && <span style={{ color: '#999' }}> ðŸ”’ Locked (Upcoming)</span>}
              {!isUpcoming && !isUnlocked(lesson) && (
                <span style={{ color: '#999' }}>
                  {' '}
                  (Locked until {new Date(lesson.release_date).toLocaleString()})
                </span>
              )}
              {progress.includes(lesson.id) && (
                <span style={{ color: 'green', marginLeft: 8 }}>âœ“</span>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Main content */}
      <div style={{ flex: 1 }}>
        {selected ? (
          isUpcoming ? (
            <div
              style={{
                textAlign: 'center',
                padding: 40,
                color: '#666',
                background: '#f9f9f9',
                border: '1px solid #ddd',
                borderRadius: 8,
              }}
            >
              <h3>{selected.title}</h3>
              <p>
                ðŸš« Content locked until{' '}
                <b>{new Date(course.start_date).toLocaleDateString()}</b>
              </p>
            </div>
          ) : (
            <>
              <h3>{selected.title}</h3>
              {selected.content_type === 'video' && (
                <video width="100%" height="480" controls>
                  <source
                    src={
                      selected.content_url.startsWith('http')
                        ? selected.content_url
                        : `http://localhost:5001${selected.content_url}`
                    }
                    type="video/mp4"
                  />
                  Your browser does not support the video tag.
                </video>
              )}
              {selected.content_type === 'pdf' && (
                <iframe
                  src={
                    selected.content_url.startsWith('http')
                      ? selected.content_url
                      : `http://localhost:5001${selected.content_url}`
                  }
                  width="100%"
                  height="600"
                  title={selected.title}
                />
              )}
              {selected.content_type === 'text' && (
                <div
                  dangerouslySetInnerHTML={{ __html: selected.content_url }}
                />
              )}
              <div style={{ marginTop: 12 }}>
                <button onClick={() => markDone(selected.id)}>
                  Mark Completed
                </button>
              </div>
            </>
          )
        ) : (
          <p>Select a lesson</p>
        )}
      </div>
    </div>
  );
}
