import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function CourseDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);
  const [enrolled, setEnrolled] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to access courses');
      navigate('/login');
      return;
    }
    checkEnrollment();
    fetchCourse();
    fetchLessons();
  }, [id]);

  async function checkEnrollment() {
    try {
      const res = await API.get('/enrollments');
      const enrolledIds = res.data.map(c => c.id);
      setEnrolled(enrolledIds.includes(Number(id)));
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchCourse() {
    try {
      const res = await API.get(`/courses/${id}`);
      setCourse(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchLessons() {
    try {
      const res = await API.get(`/lessons/${id}`);
      setLessons(res.data);
      if (res.data[0]) setSelected(res.data[0]);
    } catch (e) {
      console.error(e);
    }
  }

  async function enroll() {
    try {
      await API.post('/enrollments', { course_id: id });
      alert('✅ Enrolled successfully!');
      setEnrolled(true);
    } catch (e) {
      alert('Error enrolling');
    }
  }

  return (
    <div style={{ display: 'flex', gap: 20, padding: 20, backgroundColor: '#f8fafc', minHeight: '100vh' }}>
      <div style={{ width: 320, backgroundColor: '#fff', padding: 16, borderRadius: 8, boxShadow: '0 2px 6px rgba(0,0,0,0.1)' }}>
        <h2 style={{ color: '#f97316' }}>{course?.title}</h2>
        <p style={{ color: '#374151', fontSize: 14 }}>{course?.description}</p>

        {!enrolled ? (
          <div style={{ marginTop: 20 }}>
            <p style={{ color: 'red', fontWeight: 500 }}>You must enroll to view lessons.</p>
            <button onClick={enroll} style={{
              backgroundColor: '#f97316',
              color: '#fff',
              border: 'none',
              padding: '6px 12px',
              borderRadius: 6,
              cursor: 'pointer'
            }}>Enroll Now</button>
          </div>
        ) : (
          <>
            <h3 style={{ marginTop: 20 }}>Lessons</h3>
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {lessons.map(l => (
                <li key={l.id}
                  style={{
                    padding: 8,
                    borderRadius: 6,
                    background: selected?.id === l.id ? '#fde68a' : '#fff',
                    cursor: 'pointer',
                    border: '1px solid #e5e7eb',
                    marginBottom: 6
                  }}
                  onClick={() => setSelected(l)}>
                  {l.position}. {l.title}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>

      <div style={{ flex: 1, background: '#fff', borderRadius: 8, padding: 20, boxShadow: '0 2px 6px rgba(0,0,0,0.1)' }}>
        {enrolled ? (
          selected ? (
            <>
              <h3>{selected.title}</h3>
              {selected.content_type === 'video' && (
                <video width="100%" height="480" controls>
                  <source
                    src={
                      selected.content_url.startsWith('http')
                        ? selected.content_url
                        : `http://localhost:5001${selected.content_url}`
                    }
                    type="video/mp4"
                  />
                  Your browser does not support the video tag.
                </video>
              )}
              {selected.content_type === 'pdf' && (
                <iframe
                  src={
                    selected.content_url.startsWith('http')
                      ? selected.content_url
                      : `http://localhost:5001${selected.content_url}`
                  }
                  width="100%"
                  height="600"
                  title={selected.title}
                />
              )}
              {selected.content_type === 'text' && (
                <div dangerouslySetInnerHTML={{ __html: selected.content_url }} />
              )}
            </>
          ) : <p>Select a lesson</p>
        ) : (
          <p style={{ color: '#6b7280' }}>Login and enroll to view this course.</p>
        )}
      </div>
    </div>
  );
}
