import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import API from "../api";

export default function Dashboard() {
  const [available, setAvailable] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [enrolled, setEnrolled] = useState([]);
  const nav = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) {
      fetchEnrolled().then(() => {
        fetchCourses();
      });
    } else {
      fetchCourses();
    }
  }, [token]);

  async function fetchCourses() {
    try {
      const res = await API.get("/courses");
      const today = new Date();
      const started = res.data.filter((c) => new Date(c.start_date) <= today);
      const upcomingCourses = res.data.filter(
        (c) => new Date(c.start_date) > today
      );

      const filteredStarted = started.filter(
        (course) => !enrolled.some((e) => e.id === course.id)
      );

      setAvailable(filteredStarted);
      setUpcoming(upcomingCourses);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchEnrolled() {
    try {
      const res = await API.get("/enrollments");
      setEnrolled(res.data);
      return res.data;
    } catch (e) {
      console.error(e);
    }
  }

  async function enroll(courseId) {
    if (!token) {
      alert("Please login to enroll in a course.");
      nav("/login");
      return;
    }

    try {
      await API.post("/enrollments", { course_id: courseId });
      alert("âœ… Enrolled successfully!");
      await fetchEnrolled();
      fetchCourses();
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.message || "Error");
    }
  }

  async function handleUnenroll(courseId) {
    if (!window.confirm("Are you sure you want to unenroll from this course?")) return;
    try {
      await API.delete(`/enrollments/${courseId}`);
      alert("âŒ Unenrolled successfully!");
      await fetchEnrolled();
      fetchCourses();
    } catch (e) {
      console.error(e);
      alert("Error while unenrolling");
    }
  }

  function isEnrolled(courseId) {
    return enrolled.some((c) => c.id === courseId);
  }

  return (
    <div
      style={{
        padding: 20,
        fontFamily: "Inter, system-ui, -apple-system, sans-serif",

        minHeight: "100vh",
      }}
    >
      <h1
        style={{
          color: "#ff6f00",
          fontSize: "2.8rem",
          fontWeight: "800",
          marginBottom: 30,
          textAlign: "center",
          letterSpacing: "1px",
        }}
      >
        EduTrack ðŸ“š
      </h1>

      {/* ðŸŒ¿ Enrolled Courses */}
      {token && (
        <Section title="Your Enrolled Courses">
          {enrolled.length === 0 ? (
            <EmptyState text="You haven't enrolled in any course yet." />
          ) : (
            <CourseGrid>
              {enrolled.map((c) => (
                <CourseCard
                  key={c.id}
                  title={c.title}
                  instructor={c.instructor}
                  description={c.description}
                  primaryAction={
                    <Link
                      to={`/course/${c.id}`}
                      style={styles.greenButton}
                    >
                      Go to Course
                    </Link>
                  }
                  secondaryAction={
                    <button
                      onClick={() => handleUnenroll(c.id)}
                      style={styles.redButton}
                    >
                      Unenroll
                    </button>
                  }
                />
              ))}
            </CourseGrid>
          )}
        </Section>
      )}

      {/* ðŸŒ¿ Available Courses */}
      <Section title="Available Courses">
        {available.length === 0 ? (
          <EmptyState text="No courses have started yet." />
        ) : (
          <CourseGrid>
            {available.map((c) => (
              <CourseCard
                key={c.id}
                title={c.title}
                instructor={c.instructor}
                description={c.description}
                primaryAction={
                  <button
                    onClick={() => enroll(c.id)}
                    style={styles.orangeButton}
                  >
                    Enroll
                  </button>
                }
              />
            ))}
          </CourseGrid>
        )}
      </Section>

      {/* ðŸŒ¿ Upcoming Courses */}
      <Section title="Upcoming Courses">
        {upcoming.length === 0 ? (
          <EmptyState text="No upcoming courses." />
        ) : (
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              display: "grid",
              gap: 12,
            }}
          >
            {upcoming.map((u) => (
              <li
                key={u.id}
                style={{
                  padding: 14,
                  borderRadius: 12,
                  backgroundColor: "#ffffff",
                  boxShadow: "0 2px 10px rgba(0,0,0,0.06)",
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <span style={{ fontSize: 15, color: "#374151" }}>
                  <strong>{u.title}</strong> (starts{" "}
                  {new Date(u.start_date).toLocaleDateString()})
                </span>
                <button
                  disabled
                  style={{
                    backgroundColor: "#9ca3af",
                    color: "#fff",
                    padding: "6px 12px",
                    borderRadius: 8,
                    border: "none",
                    opacity: 0.6,
                    cursor: "not-allowed",
                  }}
                >
                  Locked
                </button>
              </li>
            ))}
          </ul>
        )}
      </Section>
    </div>
  );
}

/* âœ… Reusable Section */
function Section({ title, children }) {
  return (
    <section style={{ marginBottom: 50 }}>
      <h2
        style={{
          fontSize: "1.7rem",
          marginBottom: 20,
          color: "#1f2937",
          fontWeight: "700",
        }}
      >
        {title}
      </h2>
      {children}
    </section>
  );
}

/* âœ… Reusable Course Grid */
function CourseGrid({ children }) {
  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fill,minmax(300px,1fr))",
        gap: 20,
      }}
    >
      {children}
    </div>
  );
}

/* âœ… Reusable Card Component */
function CourseCard({ title, instructor, description, primaryAction, secondaryAction }) {
  return (
    <div
      style={{
        padding: 20,
        borderRadius: 16,
        backgroundColor: "#ffffff",
        boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
        transition: "transform 0.2s ease, box-shadow 0.2s ease",
        cursor: "default",
      }}
      onMouseEnter={(e) => (e.currentTarget.style.transform = "translateY(-4px)")}
      onMouseLeave={(e) => (e.currentTarget.style.transform = "translateY(0)")}
    >
      <h3
        style={{
          fontSize: "1.3rem",
          fontWeight: "700",
          color: "#1f2937",
          marginBottom: 6,
        }}
      >
        {title}
      </h3>
      <p style={{ fontSize: 13, fontWeight: 500, color: "#6b7280" }}>
        {instructor}
      </p>
      <p
        style={{
          marginTop: 8,
          fontSize: 14,
          color: "#4b5563",
          lineHeight: 1.5,
        }}
      >
        {description?.slice(0, 120)}...
      </p>
      <div
        style={{
          marginTop: 14,
          display: "flex",
          gap: 10,
          justifyContent: secondaryAction ? "space-between" : "flex-start",
        }}
      >
        {primaryAction}
        {secondaryAction}
      </div>
    </div>
  );
}

/* âœ… Reusable Empty State */
function EmptyState({ text }) {
  return (
    <p style={{ color: "#6b7280", fontStyle: "italic", fontSize: 15 }}>
      {text}
    </p>
  );
}

/* âœ… Button Styles */
const styles = {
  orangeButton: {
    backgroundColor: "#f97316",
    color: "#fff",
    padding: "8px 14px",
    borderRadius: 8,
    border: "none",
    fontWeight: "600",
    cursor: "pointer",
    transition: "background 0.2s",
  },
  greenButton: {
    backgroundColor: "#10b981",
    color: "#fff",
    padding: "8px 14px",
    borderRadius: 8,
    border: "none",
    fontWeight: "600",
    textDecoration: "none",
    textAlign: "center",
    display: "inline-block",
    transition: "background 0.2s",
  },
  redButton: {
    backgroundColor: "#ef4444",
    color: "#fff",
    padding: "8px 14px",
    borderRadius: 8,
    border: "none",
    fontWeight: "600",
    cursor: "pointer",
    transition: "background 0.2s",
  },
};
