import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function CourseDetail() {
  const { id } = useParams();
  const nav = useNavigate();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);
  const [progress, setProgress] = useState([]);
  const [enrolled, setEnrolled] = useState(false);

  useEffect(() => {
    checkAuth();
    fetchCourse();
    fetchLessons();
    fetchProgress();
    checkEnrollment();
  }, [id]);

  // ✅ if not logged in, redirect to login
  function checkAuth() {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login to access courses");
      nav("/login");
    }
  }

  async function fetchCourse() {
    try {
      const res = await API.get(`/courses/${id}`);
      setCourse(res.data);
    } catch (e) { console.error(e); }
  }

  async function fetchLessons() {
    try {
      const res = await API.get(`/lessons/${id}`);
      setLessons(res.data);
      if (res.data[0]) setSelected(res.data[0]);
    } catch (e) { console.error(e); }
  }

  async function fetchProgress() {
    try {
      const res = await API.get(`/progress/course/${id}`);
      setProgress(res.data);
    } catch (e) { console.error(e); }
  }

  async function checkEnrollment() {
    try {
      const res = await API.get("/enrollments");
      const enrolledCourses = res.data.map(c => c.id);
      setEnrolled(enrolledCourses.includes(Number(id)));
    } catch (e) {
      console.error(e);
    }
  }

  async function enroll() {
    try {
      await API.post('/enrollments', { course_id: id });
      alert("Enrolled successfully!");
      setEnrolled(true);
    } catch (e) {
      alert("Error enrolling in course");
    }
  }

  async function markDone(lessonId) {
    try {
      await API.post(`/progress/lesson/${lessonId}/complete`);
      fetchProgress();
      alert('Marked complete');
    } catch (e) {
      console.error(e);
      alert('Error');
    }
  }

  function isUnlocked(l) {
    if (!l.release_date) return true;
    return new Date(l.release_date) <= new Date();
  }

  return (
    <div style={{ display: 'flex', gap: 20, padding: 20 }}>
      <div style={{ width: 320 }}>
        <h2>{course?.title}</h2>
        <p>{course?.description}</p>

        {!enrolled && (
          <div style={{ marginTop: 20 }}>
            <p style={{ color: "red" }}>⚠ You must enroll to access course materials.</p>
            <button onClick={enroll}>Enroll Now</button>
          </div>
        )}

        {enrolled && (
          <>
            <h3>Lessons</h3>
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {lessons.map(l => (
                <li key={l.id}
                  style={{ padding: 8, cursor: 'pointer', background: selected?.id === l.id ? '#eee' : 'transparent', marginBottom: 6 }}
                  onClick={() => isUnlocked(l) ? setSelected(l) : null}>
                  {l.position}. {l.title}
                  {!isUnlocked(l) && <span style={{ color: '#999' }}> (Locked until {new Date(l.release_date).toLocaleString()})</span>}
                  {progress.includes(l.id) && <span style={{ color: 'green', marginLeft: 8 }}>✓</span>}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>

      <div style={{ flex: 1 }}>
        {enrolled ? (
          selected ? (
            <>
              <h3>{selected.title}</h3>
              {selected.content_type === 'video' && (
                <video width="100%" height="480" controls>
                  <source src={selected.content_url.startsWith('http') ? selected.content_url : `http://localhost:5001${selected.content_url}`} type="video/mp4" />
                  Your browser does not support video.
                </video>
              )}
              {selected.content_type === 'pdf' && (
                <iframe src={selected.content_url.startsWith('http') ? selected.content_url : `http://localhost:5001${selected.content_url}`} width="100%" height="600" title={selected.title} />
              )}
              {selected.content_type === 'text' && (
                <div dangerouslySetInnerHTML={{ __html: selected.content_url }} />
              )}
              <div style={{ marginTop: 12 }}>
                <button onClick={() => markDone(selected.id)}>Mark Completed</button>
              </div>
            </>
          ) : <p>Select a lesson</p>
        ) : (
          <p style={{ fontStyle: "italic", color: "#666" }}>Login & enroll to unlock lessons.</p>
        )}
      </div>
    </div>
  );
}
