import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../api';

export default function Dashboard() {
  const [courses, setCourses] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [enrolled, setEnrolled] = useState([]);

  useEffect(() => { fetchAll(); fetchUpcoming(); fetchEnrolled(); }, []);

  async function fetchAll() { try { const res = await API.get('/courses'); setCourses(res.data); } catch (e) { console.error(e); } }
  async function fetchUpcoming() { try { const res = await API.get('/courses/upcoming'); setUpcoming(res.data); } catch (e) { console.error(e); } }
  async function fetchEnrolled() { try { const res = await API.get('/enrollments'); setEnrolled(res.data); } catch (e) { console.error(e); } }

  async function enroll(courseId) {
    try { await API.post('/enrollments', { course_id: courseId }); alert('Enrolled'); fetchEnrolled(); } catch (e) { console.error(e); alert(e?.response?.data?.message || 'Error'); }
  }

  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ color: '#ff6f00' }}>EduTrack</h1>

      <section>
        <h2>Your Enrolled Courses</h2>
        {enrolled.length === 0 ? <p>No enrolled courses</p> : (
          <ul>{enrolled.map(c => <li key={c.id}><Link to={`/course/${c.id}`}>{c.title}</Link></li>)}</ul>
        )}
      </section>

      <section style={{ marginTop: 20 }}>
        <h2>All Courses</h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(280px,1fr))', gap: 12 }}>
          {courses.map(c => (
            <div key={c.id} style={{ padding: 12, border: '1px solid #ddd', borderRadius: 6 }}>
              <h3><Link to={`/course/${c.id}`}>{c.title}</Link></h3>
              <p style={{ fontSize: 13 }}>{c.instructor}</p>
              <p style={{ fontSize: 13 }}>{c.description?.slice(0, 120)}</p>
              <div style={{ marginTop: 8 }}>
                <button onClick={() => enroll(c.id)}>Enroll</button>
              </div> 
            </div>
          ))}
        </div>
      </section>

      <section style={{ marginTop: 20 }}>
        <h2>Upcoming</h2>
        <ul>
          {upcoming.map(u => <li key={u.id}>{u.title} (starts {new Date(u.start_date).toLocaleString()})</li>)}
        </ul>
      </section>
    </div>
  );
}
