import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';

export default function CourseDetail() {
  const { id } = useParams();
  const [lessons, setLessons] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(()=>{ async function f(){ try{ const res = await API.get(`/lessons/${id}`); setLessons(res.data); if(res.data[0]) setSelected(res.data[0]); }catch(e){console.error(e);} } f(); },[id]);

  return (
    <div style={{display:'flex', gap:20, padding:20}}>
      <div style={{width:300}}>
        <h3>Lessons</h3>
        <ul style={{listStyle:'none', padding:0}}>
          {lessons.map(l=> (
            <li key={l.id} onClick={()=>setSelected(l)} style={{padding:10, cursor:'pointer', background: selected?.id===l.id? '#ddd':'#f5f5f5', marginBottom:8}}>{l.position}. {l.title}</li>
          ))}
        </ul>
      </div>

      <div style={{flex:1}}>
        {selected?(
          <>
            <h3>{selected.title}</h3>
            {selected.content_type==='video' && (
              <video width="100%" height="480" controls>
                <source src={selected.content_url.startsWith('http')? selected.content_url : `${window.location.origin.replace(':3000',':5001')}${selected.content_url}`} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            )}
            {selected.content_type==='pdf' && (
              <iframe src={selected.content_url.startsWith('http')? selected.content_url : `${window.location.origin.replace(':3000',':5001')}${selected.content_url}`} width="100%" height="600" title={selected.title} />
            )}
            {selected.content_type==='text' && (
              <div dangerouslySetInnerHTML={{__html: selected.content_url}} />
            )}
          </>
        ): <p>Select a lesson</p>}
      </div>
    </div>
  );
}
