import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../api';

export default function Dashboard() {
  const [courses, setCourses] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [enrolled, setEnrolled] = useState([]);

  useEffect(() => {
    fetchAll();
    fetchUpcoming();
    fetchEnrolled();
  }, []);

  async function fetchAll() {
    try {
      const res = await API.get('/courses');
      setCourses(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchUpcoming() {
    try {
      const res = await API.get('/courses/upcoming');
      setUpcoming(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchEnrolled() {
    try {
      const res = await API.get('/enrollments');
      setEnrolled(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function enroll(courseId) {
    try {
      await API.post('/enrollments', { course_id: courseId });
      alert('Enrolled successfully!');
      fetchEnrolled();
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.message || 'Error');
    }
  }

  function isEnrolled(courseId) {
    return enrolled.some(c => c.id === courseId);
  }

  return (
    <div className="font-sans p-8 bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-bold text-orange-600 mb-8">EduTrack</h1>

      {/* Enrolled Courses */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4">Your Enrolled Courses</h2>
        {enrolled.length === 0 ? (
          <p className="text-gray-500">You haven't enrolled in any course yet.</p>
        ) : (
          <div className="grid md:grid-cols-2 gap-6">
            {enrolled.map(c => (
              <div key={c.id} className="bg-white p-4 rounded shadow hover:shadow-md transition">
                <h3 className="text-xl font-bold text-gray-800">
                  <Link to={`/course/${c.id}`} className="hover:text-orange-600">{c.title}</Link>
                </h3>
                <p className="text-gray-600 mt-1">{c.instructor}</p>
                <p className="mt-2 text-gray-700">{c.description?.slice(0, 120)}...</p>
                <Link 
                  to={`/course/${c.id}`} 
                  className="mt-4 inline-block bg-orange-600 text-white px-4 py-2 rounded hover:bg-orange-700"
                >
                  Go to Course
                </Link>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* All Courses */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4">All Courses</h2>
        <div className="grid md:grid-cols-3 gap-6">
          {courses.map(c => (
            <div key={c.id} className="bg-white p-4 rounded shadow hover:shadow-lg transition">
              <h3 className="text-xl font-bold text-gray-800">{c.title}</h3>
              <p className="text-gray-600 mt-1">{c.instructor}</p>
              <p className="mt-2 text-gray-700">{c.description?.slice(0, 120)}...</p>
              <div className="mt-4">
                {isEnrolled(c.id) ? (
                  <Link 
                    to={`/course/${c.id}`} 
                    className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                  >
                    Go to Course
                  </Link>
                ) : (
                  <button 
                    onClick={() => enroll(c.id)}
                    className="bg-orange-600 text-white px-4 py-2 rounded hover:bg-orange-700"
                  >
                    Enroll
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Upcoming Courses */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Upcoming Courses</h2>
        <ul className="space-y-2">
          {upcoming.map(u => {
            const hasStarted = new Date() >= new Date(u.start_date);
            return (
              <li key={u.id} className="bg-white p-4 rounded shadow flex justify-between items-center hover:shadow-md transition">
                <span>{u.title} (starts {new Date(u.start_date).toLocaleDateString()})</span>
                {hasStarted ? (
                  <Link 
                    to={`/course/${u.id}`} 
                    className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                  >
                    Go to Course
                  </Link>
                ) : (
                  <button className="bg-gray-400 text-white px-3 py-1 rounded cursor-not-allowed opacity-60">
                    Locked
                  </button>
                )}
              </li>
            );
          })}
        </ul>
      </section>
    </div>
  );
}
