import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import API from '../api';

export default function Dashboard() {
  const [available, setAvailable] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [enrolled, setEnrolled] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to view dashboard.');
      navigate('/login');
      return;
    }
    fetchCourses();
    fetchEnrolled();
  }, []);

  async function fetchCourses() {
    try {
      const res = await API.get('/courses');
      const today = new Date();
      const started = res.data.filter(c => new Date(c.start_date) <= today);
      const upcomingCourses = res.data.filter(c => new Date(c.start_date) > today);
      setAvailable(started);
      setUpcoming(upcomingCourses);
    } catch (err) {
      console.error(err);
    }
  }

  async function fetchEnrolled() {
    try {
      const res = await API.get('/enrollments');
      setEnrolled(res.data);
    } catch (err) {
      console.error(err);
    }
  }

  async function enroll(courseId) {
    try {
      await API.post('/enrollments', { course_id: courseId });
      alert('âœ… Enrolled successfully!');
      fetchEnrolled();
    } catch (err) {
      alert(err?.response?.data?.message || 'Error');
    }
  }

  function isEnrolled(courseId) {
    return enrolled.some(c => c.id === courseId);
  }

  return (
    <div style={{
      padding: 20,
      fontFamily: "'Segoe UI', sans-serif",
      backgroundColor: '#f9fafb',
      minHeight: '100vh'
    }}>
      <h1 style={{
        color: '#f97316',
        fontSize: '2.5rem',
        fontWeight: 700,
        textAlign: 'center',
        marginBottom: 40
      }}>EduTrack Dashboard</h1>

      {/* Enrolled */}
      <section style={{ marginBottom: 40 }}>
        <h2 style={{ fontSize: '1.8rem', marginBottom: 15 }}>Your Enrolled Courses</h2>
        {enrolled.length === 0 ? (
          <p style={{ color: '#6b7280' }}>You havenâ€™t enrolled in any course yet.</p>
        ) : (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill,minmax(300px,1fr))',
            gap: 20
          }}>
            {enrolled.map(c => (
              <div key={c.id} style={{
                backgroundColor: '#fff',
                padding: 16,
                borderRadius: 10,
                boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
              }}>
                <h3 style={{ fontSize: '1.2rem', fontWeight: 600, color: '#111827' }}>
                  <Link to={`/course/${c.id}`} style={{ textDecoration: 'none', color: '#111827' }}>{c.title}</Link>
                </h3>
                <p style={{ color: '#374151', fontSize: 13, fontWeight: 500 }}>{c.instructor}</p>
                <p style={{ color: '#6b7280', fontSize: 13 }}>{c.description?.slice(0, 120)}...</p>
                <Link to={`/course/${c.id}`} style={{
                  backgroundColor: '#10b981',
                  color: '#fff',
                  padding: '6px 12px',
                  borderRadius: 6,
                  textDecoration: 'none',
                  fontWeight: 500
                }}>Go to Course</Link>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Available Courses */}
      <section style={{ marginBottom: 40 }}>
        <h2 style={{ fontSize: '1.8rem', marginBottom: 15 }}>Available Courses</h2>
        {available.length === 0 ? (
          <p style={{ color: '#6b7280' }}>No active courses yet.</p>
        ) : (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill,minmax(300px,1fr))',
            gap: 20
          }}>
            {available.map(c => (
              <div key={c.id} style={{
                backgroundColor: '#fff',
                padding: 16,
                borderRadius: 10,
                boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
              }}>
                <h3 style={{ fontSize: '1.2rem', fontWeight: 600, color: '#111827' }}>{c.title}</h3>
                <p style={{ fontSize: 13, color: '#374151' }}>{c.instructor}</p>
                <p style={{ fontSize: 13, color: '#4b5563' }}>{c.description?.slice(0, 120)}...</p>
                <div style={{ marginTop: 10 }}>
                  {isEnrolled(c.id) ? (
                    <Link to={`/course/${c.id}`} style={{
                      backgroundColor: '#10b981',
                      color: '#fff',
                      padding: '6px 12px',
                      borderRadius: 6,
                      textDecoration: 'none'
                    }}>Go to Course</Link>
                  ) : (
                    <button onClick={() => enroll(c.id)} style={{
                      backgroundColor: '#f97316',
                      color: '#fff',
                      border: 'none',
                      padding: '6px 12px',
                      borderRadius: 6,
                      cursor: 'pointer'
                    }}>Enroll</button>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Upcoming */}
      <section>
        <h2 style={{ fontSize: '1.8rem', marginBottom: 15 }}>Upcoming Courses</h2>
        {upcoming.length === 0 ? (
          <p style={{ color: '#6b7280' }}>No upcoming courses.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, display: 'grid', gap: 12 }}>
            {upcoming.map(u => (
              <li key={u.id} style={{
                backgroundColor: '#fff',
                padding: 12,
                borderRadius: 8,
                boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <span>{u.title} (starts {new Date(u.start_date).toLocaleDateString()})</span>
                <button disabled style={{
                  backgroundColor: '#9ca3af',
                  color: '#fff',
                  padding: '6px 12px',
                  borderRadius: 6,
                  border: 'none',
                  opacity: 0.7
                }}>Locked</button>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}
